import React, { Component } from 'react';
import { Container, Jumbotron } from 'react-bootstrap';
import NavBar from './components/NavBar';
import WidgetList from './components/WidgetList';
import CurrencyList from './components/CurrencyList';
import AssetTable from './components/AssetTable';

import axios from 'axios';

class App extends Component {
  constructor() {
    super();
    this.state = {
      overview: [],
      currencies: [],
      assets: [],
    };
  }

  componentDidMount() {
    axios.get('./mockData.json')
      .then((res) => {
        this.setState({
          overview: res.data.overview,
          currencies: res.data.currencies,
          assets: res.data.assets,
        })
      }).catch((err) => console.log(err));

    // TODO: checks for status OK
  }

  render() {
    console.log(this.state.overview);
    console.log(this.state.currencies);
    console.log(this.state.assets);
    return (
      <div>
        <NavBar />
        <Jumbotron>
          <Container>
            <h1>Best Bank</h1>
          </Container>
        </Jumbotron>
        <WidgetList data={this.state.overview} />
        <Container className="border border-white rounded rounded-sm bg-white my-3">
          <h1 className="text-center">My Portfolio</h1>
          <h3>Currencies</h3>
          <CurrencyList data={this.state.currencies} />
        </Container>
        <Container className="border border-white rounded rounded-sm bg-white my-3">
          <h1 className="text-center">Asset Allocation</h1>
          <AssetTable data={this.state.assets} />
        </Container>
      </div>
    );
  }
}

export default App;
